package main import ( &#34;bufio&#34; &#34;os&#34; &#34;fmt&#34; ) func main() { if len(os.Args) &gt; 1 { // we have command line args  for _, arg := range os.Args[1:] { fmt.Println(arg) } } else { // read from STDIN  stdin := bufio.NewScanner(os.Stdin) for stdin.Scan() { fmt.Println(stdin.Text()) } } } Perl
